import jsencrypt from 'jsencrypt';

const _jsencrypt = new jsencrypt(),
      _KEY_ENCRYPT = '',
      _KEY_DECRYPT = ''
;

// 加密
export function encryptStr(str) {

  _jsencrypt.setPublicKey(_KEY_ENCRYPT);

  return _jsencrypt.encrypt(str);

}

// 解密
export function decryptStr(str) {

  _jsencrypt.setPublicKey(_KEY_DECRYPT);

  return _jsencrypt.decrypt(str);

}
